$(document).ready(function(){$("#Clock").myplugin("#BtStart","#BtClear",true);var b=$("#Clock").data("myplugin");b.SetTimer(0,20,0);$("#BtClear").click(function(){b.ButtonClear();b.SetTimer(0,20,0)});$("#BtStart").click(function(){var d=$("#Clock").html();if(d=="00:00:00"){return}var a=$(this).html();if(a=="Start"){b.ButtonStart();b.setEarlyRemind(0,5,0)}else{if(a=="Pause"){b.ButtonPause()}else{b.ButtonContinue()}}})});