$(document).ready(function(){$("#Clock").myplugin("#BtStart","#BtClear",true);var c=$("#Clock").data("myplugin");c.SetTimer(0,3,0);$("#BtClear").click(function(){c.ButtonClear();c.SetTimer(0,3,0)});$("#BtStart").click(function(){var a=$("#Clock").html();if(a=="00:00:00"){return}var b=$(this).html();if(b=="Start"){c.ButtonStart()}else{if(b=="Pause"){c.ButtonPause()}else{c.ButtonContinue()}}});$("#Clock2").myplugin("#BtStart2","#BtClear2",true);var d=$("#Clock2").data("myplugin");d.SetTimer(0,20,0);$("#BtClear2").click(function(){d.ButtonClear();d.SetTimer(0,20,0)});$("#BtStart2").click(function(){var a=$("#Clock2").html();if(a=="00:00:00"){return}var b=$(this).html();if(b=="Start"){d.ButtonStart();d.setEarlyRemind(0,5,0)}else{if(b=="Pause"){d.ButtonPause()}else{d.ButtonContinue()}}})});